package java1;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//addition
int x ;
System.out.println(x=10+20);
//subtracion
int y;
System.out.println(y=20-10);
//multiplication
System.out.println(x=2*6);
//division
System.out.println(x=20/10);
//reminder
System.out.println(x=20%10);
x +=5;
//increment
int z =20;
System.out.println(++z);
//decrement
System.out.println(--z);
//adding by date names
int m = x+y+z;
System.out.println("output =" +m);
//string
String txt = "Hello this is \"pavani\" here";
System.out.println(txt);
String txt1 = "Hello this is \'pavani\' here";
System.out.println(txt1);
String txt2 = "Hello this is \\pavani\\ here";
System.out.println(txt2);
	}

}
